import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, ret=['Device', 'User', 'DID', 'borderWidth']) {

  // data['case'], ['true'], ['false']
  
  let results = {}
  
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? data['default']['display']['case'][type.toLowerCase()] : data['default']['display']['case']['borderWidth']
      bool.forEach((value, i) => {
        console.log(Object.keys(data['default']['display'][Boolean(value)])[i])
        data['default']['display'][Object.keys(data['default']['display'][Boolean(value)])[i]]['highlighted'] = Boolean(value)
        data['default']['display'][Object.keys(data['default']['display'][Boolean(value)])[i]]['style'] = style[data['default']['display'][Boolean(value)][Object.keys(data[Boolean(value)])[i]]]
        // 'displayStyle': display[Boolean(value)][Object.keys(display[Boolean(value)])[i]]}
      })
    }
  })

  console.log(data)

  return results
}
