import * as style from "../../css/light/focusedList";
// import AssociatedItem from '../searchList/associatedItem';
import FocusedTitle from "./focusedTitle"
import FocusedHistory from "./focusedHistory";
import { useRef } from "react";

let focusedDataTitle = {
  backgroundColor: "#4382BC",
  textAlign: "center",
}

let arrowRight = {
  display: "inline-block", 
  width: "5px", 
  height: "5px", 
  transform: "rotate(45deg)", 
  margin: "4px 0 0 5px",
  borderRight: "3px solid #4382BC", 
  borderTop: "3px solid #4382BC", 
}

export default function FocusedList({focused, setFocused}) {
  if (!focused[0]) return <div></div>
  let focusedItem = require(`../../data/${focused[1]}/${focused[0]}.json`)

  function ProfileJSON() {
    const dataRef = useRef(null)

    return (
      <div ref={dataRef} style={{backgroundColor: "white"}}>
        {getJsontest()}
      </div>
    )
  }

  function FocusedData() {
    return (
      <div style={{overflow: "hidden", display: "flex"}}>                                     {/*height: 398px*/}
        <div style={{width: "97%", margin: "12px auto 0px auto", border: "1px solid black", backgroundColor: "white"}}>
          <div style={focusedDataTitle}>Current Profile</div>
          <ProfileJSON/>
        </div>  
      </div>
    )
  }

  function stringify(data) {
    if (!data) return "undefined"
    if (data === null) return "undefined"

    if (typeof(data) === "object") {
      return Object.keys(data).map((item, i) => (
        <div>
          {item}
          <div>
            {item}
            {stringify(data[item])}
          </div>
        </div>
      ))
    }
    else return <div>{data}</div>
  }

  function test(item) {
    if (typeof(focusedItem.resources[0].data[item]) === "object") {
      return (
        <div style={{display: "flex", cursor: "pointer"}}>
          <div>{item}</div>
          <div style={arrowRight}></div>
      </div>
      )
    }

    // border: solid black;
    // border-width: 0 3px 3px 0;
    // display: inline-block;
    // padding: 3px;

    else return (
      <div style={{display: "flex"}}>
        <div style={{width: "50%"}}>
          {item}
        </div>
        <div>
          {focusedItem.resources[0].data[item]}
        </div>
      </div>
    )
  }

  function getJsontest() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div 
        onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
        onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}  
        style={{fontSize: "11px", width: "100%"}}>{test(item)}</div>
    ))
  }

  function getJson() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div style={{display: "flex", width: "100%"}}>
        <div style={{fontSize: "11px", textIndent:"3px", width: "40%"}}>
          {item}
        </div>
        <div style={{fontSize: "11px"}}>
          {stringify(focusedItem.resources[0].data[item])}
        </div>
      </div>
    ))
  }

  if (focusedItem.resources[0].data.associatedDevices) {
    return (
      <div style={style.focusedContainer}>
        <FocusedTitle setFocused={setFocused} focusedItem={focusedItem}/>
        <FocusedData/>
        <FocusedHistory focusedItem={focusedItem}/>
      </div>
    )
  }
}
