import axios from 'axios';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getLineData from "./getLineData";
import getVoicemailData from "./getVoicemailData"
import getLasData from './getLasData';
import getDidData from './getDidData';

export default async function callApi(loadingBar, setLoadingBar) {
  let loadingWidth = 0
  let rawData = {}
  let cleanData = {}
  let locationHierarchy = {}

  let res = await axios.get(`http://localhost:5000/phones`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['phones'] = res['data']
    cleanData['phones'] = await getDeviceData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/subscribers`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['subscribers'] = res['data']
    cleanData['subscribers'] = await getUserData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/lines`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['lines'] = res['data']
    cleanData['lines'] = await getLineData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/voicemail`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['voicemail'] = res['data']
    cleanData['voicemail'] = await getVoicemailData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/did`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['did'] = res['data']
    cleanData['did'] = await getDidData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/las`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    rawData['las'] = res['data']
    cleanData['las'] = await getLasData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/location_hierarchy`)
  try {
    loadingWidth+=20
    setLoadingBar({...loadingBar, width: loadingWidth})
    locationHierarchy = res['data']
  }
  catch {console.log(res.status)}

  return new Promise((resolve, reject) => {
    resolve({
      "rawData": rawData,
      "cleanData": cleanData, 
      "locationHierarchy": locationHierarchy
    })
    reject("fail")
  })
}
