import hierarchy from "../../data/location_hierarchy.json"

export default function getHcsLink(item, type) {
  let link = ""
  let store = ""
  try {
    let file = require(`../../data/${type}/${item}.json`)
    let pkid = file.resources[0].meta.pkid

    
    if (file.resources[0].meta.hierarchy.includes("Head Office")) store = "head_office"
    else store = "store_" + file.resources[0].meta.hierarchy.split("Store ")[1]
    
    link = hierarchy[`link_${type}`]
    link = link.replace("{1}", hierarchy.stores[store])
    link = link.replace("{2}", pkid)
  }

  catch {/*catch non-existing device*/}

  console.log(link)

  return link
}
