import axios from 'axios';

export default async function callApi() {
  return axios.get(`${process.env.REACT_APP_DEV_URL}ldps_workers`)
    .then((res, err) => {
      return new Promise((resolve, reject) => {
        resolve(res.new_data)
        reject(err)
      })
    })
};
