import profiles from "../../data/allstream_profile_list.json";
import getHcsLink from "./getHcsLink";

export default function getDeviceData() {
  let profileList = [];

  profiles['devices'].forEach((device, i) => {
    const data = require(`../../data/devices/${device}.json`);

    profileList.push([
      {
        key: i,
        type: "mac",
        index: "MAC Address",
        data: function() {
          try {return data.resources[0].data.name.toUpperCase()} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "product",
        index: "Product",
        data: function() {
          try {return data.resources[0].data.product} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "user",
        index: "User ID",
        data: function() {
          try {return data.resources[0].data.ownerUserName} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "description",
        index: "Description",
        data: function() {
          try {return data.resources[0].data.description} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "primary",
        index: "Primary Line",
        data: function() {
          try {return data.resources[0].data.lines.line[0].dirn.pattern} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "mask",
        index: "E164 Mask",
        data: function() {
          try {return data.resources[0].data.lines.line[0].e164Mask} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "location",
        index: "Location",
        data: function() {
          try {return data.resources[0].meta.hierarchy.split("_").pop()} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "link",
        index: "Link",
        data: getHcsLink(data.resources[0].data.name, "devices")
      }
    ]);
  });

  return profileList;
};
