let itemContainer = {
    display: 'flex',
    height: 45, 
    width: 800, 
    backgroundColor: '#e4f7ff',
    margin: '8px 0 0 10px',
    borderRadius: '10px 5px 5px 10px',
    textTransform: 'capitalize',
}

let isd = {
    width: '15%',
    minWidth: '15%',
    color: '#e4f7ff',
    textAlign: 'center',
    paddingTop: 10,
    height: 35,
    fontSize: 18,
    backgroundColor: '#063b50',
    borderRadius: '5px 0 0 5px'
}

export default function Tickets({data}) {
    function renderItems() {
        return Object.keys(data.items).map((item, i) => (
            <div key={`Ti${i}`}>{item} {data.items[item]}</div>
        ))
    }

    return (
        <div style={itemContainer}>
            <div style={isd}>{data.isd}</div>

            <div style={{width: '85%', display: 'flex', justifyContent: 'space-between'}}>
                <div style={{marginLeft: 5}}>
                    <div style={{display: 'flex', justifyContent: 'space-between'}}>
                        <div style={{width: 300}}>{data.date}</div>
                        <div>{data.status}</div>

                    </div>

                    <div style={{width: 300, backgroundColor: 'black', height:1}}></div>

                    <div style={{overflow: 'ellipsis'}}>Subject: {data.subject}</div>
                </div>

                {/* <div style={status}>{data.status}</div> */}
            </div>
        </div>
    )
}
