export default function handleSearchTypeChange(targ='Device', data, ret=['Device', 'User', 'DID', 'borderWidth']) {
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? data['display']['case'][type.toLowerCase()] : data['display']['case']['borderWidth']
      bool.forEach((value, i) => {
        data['display'][Object.keys(data['display'][Boolean(value)])[i]]['highlighted'] = Boolean(value)
        data['display']['style'][Object.keys(data['display'][Boolean(value)])[i]] = data['display'][Boolean(value)][Object.keys(data['display'][Boolean(value)])[i]]
      })
    }
  })

  return data
}
