import {React, useEffect, useState} from 'react';
import * as style from "../css/light/main";
import * as loadingStyle from "../css/light/loading"
import getDeviceData from "./dataRetrieval/getDeviceData";
import getUserData from "./dataRetrieval/getUserData";
import getLineData from "./dataRetrieval/getLineData";
import Focused from "./focused/focused";
import Loading from "./loading/loading";
// import getDidData from "./dataRetrieval/getDidData";
import ProfileList from "./profileList/profileList"
import ProfileButtons from "./profileButtons/profileButtons"
import ProfileTotal from "./profileTotal/profileTotal"
import axios from 'axios';

const loadingAnimation = (setLoadingText) => {
  let string = "Loading"
  let value = 0

  let loadingInterval = setInterval(() => {
    if (value >= 3) {
      value = 0
      string = "Loading"
    }
    else string = string + "."
    
    value++

    console.log("run")

    setLoadingText(string)
    return loadingInterval
  }, 1200)
}

const callApi = async () => {
  let cont = {}

  let res = await axios.get(`http://localhost:5000/devices`)
    try {
      console.log(res.status)
      cont['devices'] = await getDeviceData(res)
    }
    catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/users`)
  try {
    console.log(res.status)
    cont['users'] = await getUserData(res)
  }
  catch {console.log(res.status)}

  res = await axios.get(`http://localhost:5000/lines`)
  try {
    console.log(res.status)
    cont['lines'] = await getLineData(res)
  }
  catch {console.log(res.status)}

  return new Promise((resolve, reject) => {
    resolve(cont)
    reject("fail")
  })
}

//Extends Main
export default function Main() {
  const [profileData, setProfileData] = useState([])
  const [listToRender, setListToRender] = useState(["Devices", "Users", "Lines"])
  const [profileDataToRender, setProfileDataToRender] = useState([])
  const [totalToRenderNumber, setTotalToRenderNumber] = useState(50)
  const [focused, setFocused] = useState([])
  const [loading, setLoading] = useState(loadingStyle.loading)
  const [loadingText, setLoadingText] = useState("Loading")

  useEffect(() => {
    const getData = async (loadingInterval) => {
      let res = await callApi()
      setProfileData(res)
      setProfileDataToRender(res)
      setLoading({...loadingStyle.loading, visibility: "hidden"})
      console.log(loadingInterval)
    }
    let loadingInterval = loadingAnimation(setLoadingText)
    getData(loadingInterval)
    clearInterval(loadingInterval)
  }, [])

  return (
    <div style={style.main}>
      <Loading loading={loading} loadingText={loadingText} setLoadingText={setLoadingText}/>
      <ProfileButtons
        listToRender={listToRender}
        setListToRender={setListToRender}/>
      <div style={style.containerLeft}>
        <ProfileList 
          focused={focused}
          setFocused={setFocused}
          listToRender={listToRender}
          profileData={profileData}
          profileDataToRender={profileDataToRender}
          setProfileDataToRender={setProfileDataToRender}
          totalToRenderNumber={totalToRenderNumber}/>
        <Focused focused={focused} setFocused={setFocused}/>
      </div>
      <ProfileTotal 
        listToRender={listToRender}
        profileDataToRender={profileDataToRender} 
        totalToRenderNumber={totalToRenderNumber} 
        setTotalToRenderNumber={setTotalToRenderNumber}/>
    </div>
  )
};
