import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, displayStyle, ret=['Device', 'User', 'DID', 'borderWidth']) {
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? data['display']['case'][type.toLowerCase()] : data['display']['case']['borderWidth'];
      
      bool.forEach((value, i) => {
        console.log(data['display'][Object.keys(data['display'][Boolean(value)])[i]]['highlighted'])
        data['display'][Object.keys(data['display'][Boolean(value)])[i]]['highlighted'] = Boolean(value);
        data['display'][Object.keys(data['display'][Boolean(value)])[i]] = data['display'][Boolean(value)][Object.keys(data['display'][Boolean(value)])[i]];
      });
    };
  });

  console.log(data)

  return data;
};
