import {React, useState, useEffect} from 'react';
import * as __data from "./searchListData"
import * as style from "../../css/light/searchList";
import * as focusStyle from "../../css/light/focusedList";
import History from './history';
import AssociatedItem from './associatedItem';
import renderIndexMenu from './renderIndexMenu';
import getItem from './getItem';
import getTotalCount from './getTotalCount';
import renderList from './renderList';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import getSearch from './getSearch';
import getFilteredSearch from './getFilteredSearch';
import handleSearchTypeChange from "./handleSearchTypeChange";

let filteredSearch = {
  "device": {"results": getDeviceData, "relevant": "mac"}, 
  "user": {"results": getUserData, "relevant": "id"},
  "did": {"results": getDidData, "relevant": "did"}
}

let testFocused = []

export default function SearchList() {
  const [data, setData] = useState(__data['default'])
  const [item, setItem] = useState(getItem(filteredSearch))
  const [focused, setFocused] = useState([])
  const [pageNumber] = useState(1)
  const [tempItem, setTempItem] = useState("")

  useEffect(() => {
    setData(getTotalCount(filteredSearch, data))
  }, [data])

  const handleInputChange = string => {
    let search = getSearch(getDeviceData, getUserData, getDidData, string, filteredSearch)
    filteredSearch = getFilteredSearch(search)
    setItem(getItem(filteredSearch))
    setData(getTotalCount(filteredSearch, data))
    console.log(item.device.length)
  }

  const getSearchTypeChange = (e, type) => {
    let results = handleSearchTypeChange(e, data, type)
    setData({...results})
  }

  const handleFocus = (e, type, id, assoc) => {
    if (e.target.style.cursor !== "pointer") return
    for (let i in focused) if (e.target.innerHTML === focused[i][0].resources[0].data[id]) return
    testFocused = [[require(`../../data/${type}/${e.nativeEvent.path[1].innerText.split('\n')[0]}.json`), id, type, assoc], ...testFocused]
    setFocused(testFocused) 
  }

  const handleFocusChange = (item) => {
    let temp = testFocused
    temp.splice(temp.indexOf(item), 1)
    setFocused(temp)
  }

  const renderFocusedList = () => {
    let margin = focused.length === 1 ? "0px 0px 0px 0px" : "0px 0px 5px 0px"
    let focusedItemDD = {...focusStyle.focusedItemDD, margin}

    return focused.map((item, i) => (
      <div key={i}>
        <div style={focusStyle.focusedItemTitleContainer} key={`F${i}`}>
          <div style={{width: "25%"}}></div>  
          <div style={focusStyle.focusedItemTitle}>{item[0].resources[0].data[item[1]]}</div>
          <div onClick={() => {setTempItem(item); handleFocusChange(item)}} style={focusStyle.focusClose}>+</div>
        </div>
        <div style={focusedItemDD}>
          <AssociatedItem profile={item} title={item[3]}/>
          <History profile={[item[0].resources[0].data[item[1]], item[2]]}/>
        </div>
      </div>
    ))
  }

  return (
    <div>
      <div style={{maxWidth: "63.5vw"}}>
        <div style={{display: "flex", marginBottom: "5px"}}>
          <div onClick={e => getSearchTypeChange(e, "device")} style={style[data['display']['style']['device']]}>Device</div>
          <div onClick={e => getSearchTypeChange(e, "user")} style={style[data['display']['style']['user']]}>User</div>
          <div onClick={e => getSearchTypeChange(e, "line")} style={style[data['display']['style']['line']]}>Line</div>
          <div onClick={e => getSearchTypeChange(e, "did")} style={style[data['display']['style']['did']]}>DID</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div>
        <div style={focusStyle.container}>{renderFocusedList()}</div>
        <div style={style.listContainerStyle}>
          <div>{renderIndexMenu(data['display']['device'], item['device'], filteredSearch['device']['relevant'], data.index.device, data)}</div>
          <div onClick={e => handleFocus(e, "phones", "name", "Associated Lines")}>{renderList(item.device, data['display']['device'], pageNumber, data)}</div>
          <div>{renderIndexMenu(data['display']['user'], item['user'], filteredSearch['user']['relevant'], data.index.user, data)}</div>
          <div onClick={e => handleFocus(e, "users", "userid", "Associated Devices")}>{renderList(item.user, data['display']['user'], pageNumber, data)}</div>
          <div>{renderIndexMenu(data['display']['did'], item['did'], filteredSearch['did']['relevant'], data.index.did, data)}</div>
          <div>{renderList(item.did, data['display']['did'], pageNumber, data)}</div>
        </div>
        <div style={{width: "63vw", height: "26px", backgroundColor: "#4382BC", marginTop: "7px", borderRadius: "4px", display: "flex"}}>
          <div style={{width: "200px", paddingTop: "2px", textAlign: "center"}}>Total Count </div>
          <div style={{width: "200px", paddingLeft: "2px", margin: "3px 3px 0px 2px" , backgroundColor: "white", height: "20px", borderRadius: "4px"}}>{`Device: ${item.device.length}`}</div>
          <div style={{width: "200px", paddingLeft: "2px", margin: "3px 3px 0px 2px" , backgroundColor: "white", height: "20px", borderRadius: "4px"}}>{`User: ${item.user.length}`}</div>
          <div style={{width: "200px", paddingLeft: "2px", margin: "3px 3px 0px 2px" , backgroundColor: "white", height: "20px", borderRadius: "4px"}}>{`Line: ${item.did.length}`}</div>
          <div style={{width: "200px", paddingLeft: "2px", margin: "3px 3px 0px 2px" , backgroundColor: "white", height: "20px", borderRadius: "4px"}}>{`DID: ${item.did.length}`}</div>
        </div>
      </div>
    </div>
  );
};
