import * as style from "../../css/light/searchList";
import createButtonState from "./createButtonState";
import React, {useState} from 'react';

export default function ProfileButtons({listToRender, setListToRender}) {
  const staticButtons = listToRender
  const [highlighted, setHighlighted] = useState("")
  const [defaultButtons] = useState(staticButtons)
  const [buttonStyle, setButtonStyle] = useState(createButtonState(defaultButtons, style))

  const changeFilter = (item, position) => {
    if (highlighted === item.target.innerHTML) {
      setButtonStyle(createButtonState(defaultButtons, style))
      setListToRender(defaultButtons)
      setHighlighted("")
      return
    }

    else {
      let newButtonState = createButtonState(defaultButtons, style)
      newButtonState = {...newButtonState, [position]: style.searchTypeOtherHighlight}
      setButtonStyle(newButtonState)
      setListToRender([item.target.innerHTML])
      setHighlighted(item.target.innerHTML)
    }

  }

  const renderButtons = () => {
    return defaultButtons.map((item, i) => (
      <div key={`button${i}`} onClick={e => changeFilter(e, i)} style={buttonStyle[i]}>
        {item}
      </div>
    ))
  }

  return (
    <div style={{display: "flex", marginBottom: "5px"}}>
      {renderButtons()}
    </div>
  )
}
