import getPageIndexCase from "./getPageIndexCase"
import getOverallTotal from "./getOverallTotal"
import * as pageStyle from "../../css/light/pageIndex"


export default function renderPageIndex(data, firstNum, lastNum, leftTab, rightTab, currentPage) {
  let pages = []
  let temp = ""
  let total = getOverallTotal(data)

  try {console.log(currentPage.style)}
  catch {/*first render*/}

  try {
    let pageIndexResults = getPageIndexCase(data, total, firstNum, lastNum, leftTab, rightTab)
    firstNum.current.innerHTML = pageIndexResults[0].innerHTML
    lastNum.current.innerHTML = pageIndexResults[1].innerHTML
    lastNum.current.style.display = pageIndexResults[1].style.display
    leftTab.current.style.display = pageIndexResults[2].style.display
    rightTab.current.style.display = pageIndexResults[3].style.display
  }
  catch {/*first render*/}

  for (let i = 2; i < total; i++) pages.push(i)

  const updatePageIndex = (e) => {
    temp = e.target
    e.style.color = "white"
  }

  return [(
    <div style={{display: "flex"}}>  
      <div style={pageStyle.leftArrow}/>
        <div style={{display: "flex"}}>
          <div ref={firstNum} style={pageStyle.firstPage}>1</div>
          <div ref={leftTab} style={pageStyle.leftTab}>|</div>
          <div style={{display: "flex", maxWidth: "192px", overflow: "hidden"}}>
            <div style={{display: "flex"}}>
              {pages.map((i, item) => (
                <div key={`Page${i}`} onClick={(e) => updatePageIndex(e.target)} id={i} style={pageStyle.pageNumber}>{i}</div>
                ))
              }
            </div>
          </div>
          <div ref={rightTab} style={pageStyle.rightTab}>|</div>
          <div ref={lastNum} style={pageStyle.lastPage}>{total}</div>
        </div>
      <div style={pageStyle.rightArrow}/>
    </div>
  ), temp]
}
