import users from "../../data/user_list.json"
import profiles from "../../data/allstream_profile_list.json"

let searchUser = []

for (let i in profiles) {
    for (let j in profiles[i]) {
      const data = require(`../../data/${i}/${profiles[i][j]}`)

      const validate = (data) => {
        try {
          data ? data : ""
          // if (!data) return ""
          // return data
        }
        catch {return ""}
      }

      searchUser.push([{
        key: i,
        type: "id",
        data: validate(data.resources[0].data.userid)
      }])
    }
}

