import * as style from "../../css/light/searchList";
import createButtonState from "./createButtonState";
import React, {useState} from 'react';

export default function ProfileButtons({listToRender, setListToRender}) {
  const staticButtons = listToRender
  const [buttonsToRender] = useState(staticButtons)
  const [buttonStyle, setButtonStyle] = useState(createButtonState(buttonsToRender, style))

  const changeFilter = (item, position) => {
    let temp = createButtonState(buttonsToRender, style)
    temp = {...temp, [position]: style.searchTypeOtherHighlight}
    setButtonStyle(temp)
    setListToRender([item.target.innerHTML])
  
    // let temp = listToRender

    // if (listToRender.includes(item.target.innerHTML)) {
    //   temp = temp.filter((e) => {return e !== item.target.innerHTML})
    //   setListToRender(temp)
    // }
    // else {
    //   setListToRender({})
    //   temp = temp.concat(item.target.innerHTML)
    //   setListToRender(temp.sort())
    // }
  }

  const renderButtons = () => {
    return buttonsToRender.map((item, i) => (
      <div key={`B${i}`} onClick={e => changeFilter(e, i)} style={buttonStyle[i]}>
        {item}
      </div>
    ))
  }

  return (
    <div style={{display: "flex", marginBottom: "5px"}}>
      {renderButtons()}
    </div>
  )
}
