import {React, useState} from 'react';
import * as style from "../css/searchList";
import ListDeviceItem from "./listDeviceItem";
import ListUserItem from "./listUserItem";
import IndexMenu from "./indexMenu";
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";

let string = "";
let searchDevice = getDeviceData;
let searchUser = getUserData;
let relevantSearch = "mac";
let filteredDeviceSearch = [];
let filteredUserSearch = [];

//Removes duplicates and returns a list of devices that include any data value corresponding with user input string.
const returnSearch = () => {
  let infoType = [];
  let unfilteredDeviceSearch = [];
  let unfilteredUserSearch = [];

  // searchDevice.forEach(profile => {
  //   profile.forEach(e => {
  //     // if (!e.data) e.data = "";
  //     if (e.data.toString().toLowerCase().includes(string)) {
  //       infoType.push(e.type);
  //       unfilteredDeviceSearch.push(profile);
  //     }
  //   });
  // });
  // filteredDeviceSearch = [...new Set(unfilteredDeviceSearch)];

  searchUser.forEach(profile => {
    profile.forEach(e => {
      if (e.data.toString().toLowerCase().includes(string)) {
        // infoType.push(e.type);
        unfilteredUserSearch.push(profile);
      }
    })
  })
  filteredUserSearch = [...new Set(unfilteredUserSearch)];

  //Highlights most relavent searchindex.
  let counts = [];
  infoType.forEach(e => {
    counts[e] = (counts[e] || 0) + 1;
  });
  relevantSearch = Object.keys(counts)[0];
  infoType = [];
};

export default function SearchList() {
  const [device, setDevice] = useState(searchDevice);
  const [user, setUser] = useState(searchUser);
  const [searchTypeLeftStyle, setSearchTypeLeftStyle] = useState(style.searchTypeLeftStyle)
  const [searchTypeStyle, setSearchTypeStyle] = useState(style.searchTypeStyle)
  
  const handleInputChange = e => {
    string = e.target.value;
    returnSearch();
    setDevice(filteredDeviceSearch);
    setUser(filteredUserSearch)
  }

  const handleSearchTypeChange = e => {
    if (e.target.textContent === "Device" && !e.target.style["borderWidth"]) {
      setSearchTypeLeftStyle(style.searchTypeLeftStyleHighlight)
      setSearchTypeStyle(style.searchTypeStyle)
      return
    }

    if (e.target.textContent === "User" && !e.target.style["borderWidth"]) {  
      setSearchTypeStyle(style.searchTypeStyleHighlight)
      setSearchTypeLeftStyle(style.searchTypeLeftStyle)
      return
    }

    if (e.target.style["borderWidth"]) {
      setSearchTypeLeftStyle(style.searchTypeLeftStyle)
      setSearchTypeStyle(style.searchTypeStyle)
      return
    }
  }

  //Renders a list with all devices returned to filteredSearch and passed to device state.
  const renderPhoneList = () => {
    return device.map((item, i) => (
      <ListDeviceItem 
        key={`ID${i}D`}
        mac={item[0].data}
        user={item[1].data.toLowerCase()}
        line={item[2].data}
        mask={item[3].data}
        ip={item[4].data}
        location={item[5].data.toLowerCase()}
      />
    ));
  };

  const renderUserList = () => {
    return user.map((item, i) => (
      <ListUserItem
        key={`ID${i}U`}
        id={item[0].data}
        name={item[1].data}
        primary={item[2].data}
      />
    ));
  };

  return (
    <div>
      <div style={{display: "flex"}}>
        <div state="false" onClick={e => handleSearchTypeChange(e)} style={searchTypeLeftStyle}>Device</div>
        <div state="false" onClick={e => handleSearchTypeChange(e)} style={searchTypeStyle}>User</div>
        {/* <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/> */}
      </div>
      <IndexMenu relevantSearch={relevantSearch}/>
      <div style={style.listContainerStyle}>{renderUserList()}</div>
    </div>
  );
};
