import * as style from "../../css/light/focusedList";
import img from "../../images/hcs.png"
import getHcsLink from "../dataRetrieval/getHcsLink";

export default function FocusedTitle({setFocused, focusedItem}) {
  
  function getLink(data) {
    let selectors = [['userid', 'user'], ['name', 'device']]

    for (let selector in selectors) {
      if (data[selectors[selector][0]]) {
        console.log(data[selectors[selector]]) 
        // return getHcsLink(data[selectors[selector][0]], data[selectors[selector][1]])
      }
    }
  }
  
  function getTitle(data) {
    let selectors = ['userid', 'name']

    for (let selector in selectors) {
      if (data[selectors[selector]]) {
        return data[selectors[selector]]
      }
    }
  }

  return (
    <div style={style.focusedMainTitleContainer}>
      <a href={getLink(focusedItem.resources[0].data)} style={{width: "100%"}}>
        <img src={img} style={{height: "80%", cursor: "pointer", float: "left", margin: "auto 0 auto 10px"}}></img>  
      </a>
      <div style={style.focusedMainTitle}>{getTitle(focusedItem.resources[0].data)}</div>
      <div style={{display: "flex", width: "100%"}}>
        <div onClick={() => {setFocused([])}} style={style.focusedClose}>+</div>
      </div>
    </div>
  )
}
