import {useState, useRef} from "react"
import rotateArrow from "./rotateArrow"

let arrow = {
  display: "inline-block", 
  width: "5px", 
  height: "5px", 
  transform: "rotate(45deg)",
  margin: "4px 0 0 5px",
  borderRight: "3px solid #4382BC", 
  borderTop: "3px solid #4382BC"
}

export default function FocusedDataItem({item, focusedItem}) {
  const [isDown, setIsDown] = useState(false)
  const arrowRef = useRef(null)
  
  if (typeof(focusedItem.resources[0].data[item]) === "object") {
    return (
      <div
          style={{display: "flex", cursor: "pointer"}}
          onClick={() => rotateArrow(isDown, setIsDown, arrowRef)}>
        <div>{item}</div>
        <div ref={arrowRef} style={arrow}></div>
    </div>
    )
  }

  else return (
    <div style={{display: "flex"}}>
      <div style={{width: "50%"}}>{item}</div>
      <div>{focusedItem.resources[0].data[item]}</div>
    </div>
  )
}
