export default function getPageIndexCase(data, total, firstNum, lastNum, leftTab, rightTab) {
  if (total === 0) {
    console.log("case", 1)
    firstNum.current.innerHTML = 0
    lastNum.current.style.display = "none"
    rightTab.current.style.display = "none"
    leftTab.current.style.display = "none"
  }
  if (total === 1) {
    console.log("case", 2)
    firstNum.current.innerHTML = 1
    lastNum.current.style.display = "none"
    rightTab.current.style.display = "none"
    leftTab.current.style.display = "none"
  }
  if (total > 1 && total < 9) {
    console.log("case", 3)
    firstNum.current.innerHTML = 1
    lastNum.current.style.display = total
    rightTab.current.style.display = "none"
    leftTab.current.style.display = "none"
  }
  if (total === 9) {
    firstNum.current.innerHTML = 1
    lastNum.current.style.display = total
    rightTab.current.style.display = "none"
    leftTab.current.style.display = "none"
  }
  if (total > 9) {
    firstNum.current.innerHTML = 1
    lastNum.current.style.display = "initial"
    lastNum.current.style.display = total
    rightTab.current.style.display = "initial"
    lastNum.current.innerHTML = total
  }

  return [firstNum.current, lastNum.current, leftTab.current, rightTab.current]
}
