import {useRef, React, useState, useEffect} from 'react';
import * as light_style from "../../css/light/indexMenu";

export default function IndexMenu(props) {
  console.log(props)

  const [styleType] = useState(light_style)
  const [macStyle] = useState(styleType.macStyle)
  const [liStyle] = useState(styleType.liStyle)
  const liRef = useRef([])

  const createIndex = (name, type) => {
    if (type === props.index1[1]) return <li type={type} style={macStyle} ref={e => (liRef.current = [...liRef.current, e])}>{name}</li>
    else return <li type={type} style={liStyle} ref={e => (liRef.current = [...liRef.current, e])}>{name}</li>
  }

  useEffect(() => {
    for (let i = 0; i < 6; i++) { 
      let text = liRef.current[i].type
      if (props.relevantSearch === text) {
        liRef.current[i].style.backgroundColor = "#4382BC"
      } else {
        liRef.current[i].style.backgroundColor = ""
      }
    }
  })

  return (
    <div style={light_style.containerStyle}>
        {createIndex(props.index1[0], props.index1[1])}
      <ul style={light_style.ulStyle}>
        {createIndex(props.index2[0], props.index2[1])}
        {/* <div style={light_style.indexSeparatorContainer}>
          <div style={light_style.indexSeparator}/>
        </div> */}
        {createIndex(props.index3[0], props.index3[1])}
        {/* <div style={light_style.indexSeparatorContainer}>
          <div style={light_style.indexSeparator}/>
        </div> */}
        {createIndex(props.index4[0], props.index4[1])}
        {/* <div style={light_style.indexSeparatorContainer}>
          <div style={light_style.indexSeparator}/>
        </div> */}
        {createIndex(props.index5[0], props.index5[1])}
        {/* <div style={light_style.indexSeparatorContainer}>
          <div style={light_style.indexSeparator}/>
        </div> */}
        {createIndex(props.index6[0], props.index6[1])}
        {createIndex(props.index7[0], props.index7[1])}
      </ul>
    </div>
  )
}
