import * as style from "../../css/light/searchList";
import createButtonState from "./createButtonState";
import React, {useCallback, useState} from 'react';

export default function ProfileButtons(props) {
  const [buttons] = useState(["Device", "User", "Line", "DID"])
  // const [defaultDataToRender] = useState(buttons)
  // const [filteredDataToRender, setFilteredDataToRender] = useState(buttons)
  const [buttonStyle, setButtonStyle] = useState(createButtonState(buttons, style))
  
  const addFilter = (item, position) => {
    console.log(buttonStyle[position])
    setButtonStyle(() => {
      buttonStyle[position] = style.searchTypeOtherHighlight
      return buttonStyle
    })
    // setFilteredDataToRender(filteredDataToRender.filter((e) => {return e !== item.innerHTML}))
    // item.style = style.searchTypeOtherHighlight
  }

  function ProfileButton({item, position}) {
    return (
      <div
          onClick={useCallback((e) => addFilter(e, position), [position])} 
          style={buttonStyle[position]}>
        {item}
      </div>
    )
  }

  const renderButtons = () => {
    return buttons.map((item, i) => (
      // <div 
      //     onClick={e => props.getSearchTypeChange(e, item.toLowerCase())}
      //     style={style[props.data['display']['style'][item.toLowerCase()]]}>
      //   {item}
      // </div>
      <ProfileButton
        item={item}
        position={i}
        key={`RB${i}`}
        />

    ))
  }

  return (
    <div style={{display: "flex", marginBottom: "5px"}}>
      {renderButtons()}
    </div>
  )
}
