import {useState, useRef, useEffect} from "react"
import rotateArrow from "./rotateArrow"

let arrow = {
  display: "inline-block", 
  width: "5px",
  height: "5px", 
  transform: "rotate(45deg)",
  margin: "4px 0 0 5px",
  borderRight: "3px solid #4382BC", 
  borderTop: "3px solid #4382BC"
}

function renderObject(selector, value, arrowRef, ref, indent) {

  if (value === null) return (
    <div 
        style={{display: "flex"}}
        onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
        onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}>
      <div style={{width: "50%", textIndent: `${indent*15+3}px`}}>{selector}</div>
      <div style={{width: "50%", direction: "rtl", textIndent: "3px"}}>null</div>
    </div>
  )

  if (typeof(value) === "object") {
    return (
      <div>
        <div
            onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
            onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}
            style={{display: "flex", cursor: "pointer"}} 
            onClick={() => console.log(arrowRef[ref])}>
          <div style={{textIndent: `${indent*15+3}px`}}>{selector}</div>
          <div ref={arrowRef[ref]} style={arrow}></div>
        </div>
        {function() {
          return Object.keys(value).map((new_selector) => (
            // <RenderObject selector={new_selector} value={value[new_selector]} indent={indent+1}/>
            renderObject(new_selector, value[new_selector], indent+1)
          ))
        }()}
      </div>
    )
  }
  else return (
    <div 
        style={{display: "flex"}}
        onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
        onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}>
      <div style={{width: "100%", textIndent: `${indent*15+3}px`}}>{selector}</div>
      <div style={{width: "50%", direction: "rtl", textIndent: "3px"}}>{value}</div>
    </div>
  )
}

//Extends FocusedDataList
export default function FocusedDataItem({itemObject}) {
  const [isDown, setIsDown] = useState(false)
  const [indent, setIndent] = useState(1)
  const arrowRef = useRef([])
  const objectRef = useRef([])

  return Object.keys(itemObject).map((item, i) => (
    // <RenderObject selector={item} value={itemObject} indent={0}/>
    renderObject(item, itemObject[item], arrowRef, i, 0)
  ))  
}
