import React, {useCallback} from "react"
import * as style from "../../css/light/totalBar"

export default function Total(props, {tempItem, setTempItem}) {
  const handleSearchChange = useCallback(e => {
    setTempItem(parseInt(e.target.value))
  }, [setTempItem])


  const renderTotalBarItems = () => {
    return ["Device", "User", "DID", "DID"].map((item, i ) => (
      <div style={style.totalBarItem}>{`${item}: ${props.profile[item.toLowerCase()].length}`}</div>
    ))
  }

  return (
    <div style={{display: "flex"}}>
      <div style={{width: "200px", textAlign: "center", fontSize: "15px"}}>Total Count</div>
      {renderTotalBarItems()}
      <div style={{margin: "1px 2px 0px 52px", fontSize: "14px"}}>Items/Page</div>
      <input placeholder="100" style={{width: "30px", height: "16px", margin: "3px 3px 0px 2px", paddingTop: "0px", border: "0"}} 
        onChange={e => setTempItem(parseInt(e.target.value))} 
        onKeyPress={e => {if (e.key === "Enter") setPageNumber(tempItem)}}></input>
      <div onClick={() => setPageNumber(tempItem)} style={{width: "25px", height: "17px", backgroundColor: "#e8fefd", borderRadius: "2px", marginTop: "3px", cursor: "pointer"}}>
        <div style={{width: "8px", height: "1px", backgroundColor: "black", margin: "8px 0px 0px 8px"}}></div>
      </div>
    </div>
  )
}
