import {useState, useRef, useEffect} from "react"
import rotateArrow from "./rotateArrow"

let arrow = {
  display: "inline-block", 
  width: "5px",
  height: "5px", 
  transform: "rotate(45deg)",
  margin: "4px 0 0 5px",
  borderRight: "3px solid #4382BC", 
  borderTop: "3px solid #4382BC"
}

function renderObject(item, focusedItem, isDown, setIsDown, arrowRef, firstRender=false) {
  
  if (firstRender) {
    if (typeof(focusedItem.resources[0].data[item]) === "object") {
      // if (isDown) {    
        if (focusedItem.resources[0].data[item] === null) return "undefined"
        console.log(focusedItem.resources[0].data[item])
        return Object.keys(focusedItem.resources[0].data[item]).map((newItem, i) => (
          <div
            style={{cursor: "pointer"}}
            onClick={() => {rotateArrow(isDown, setIsDown, arrowRef)}}>
          <div style={{display: "flex"}}>
            <div>{item}</div>
            <div style={arrow}></div>
          </div>
          {/* <div style={{textIndent: "30px"}}>
          {renderObject(newItem, isDown, setIsDown, arrowRef)}
          </div> */}
        </div>
        ))
      // }
    }

  
    else return (
      <div style={{display: "flex"}}>
        <div style={{width: "50%"}}>{item}</div>
        <div>{focusedItem.resources[0].data[item]}</div>
      </div>
    )
  }

  // }  
}

//Extends FocusedDataList
export default function FocusedDataItem({item, focusedItem}) {
  const [isDown, setIsDown] = useState(false)
  const arrowRef = useRef(null)
  const objectRef = useRef(null)
  const [indent, setIndent] = useState(1)

