import { useState } from 'react'
import Alerts from './alerts/alerts'
import Navigation from './navigation/navigation'
import Settings from './settings/settings'

let optionsContainer = {
    zIndex: 5,
    display: 'flex',
    width: 304,
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let optionsMenuContainer = {
    display: 'flex',
    width: 304
}

let optionsDdlContainer = {
    width: 304,
    height: 400,
    maxHeight: 0,
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px',
    transition: 'max-height 0.5s'
}

let optionsDdlIndicator = {

}

export default function Options() {
    const [ddlContainer, setDdlContainer] = useState(optionsDdlContainer)
    const [ddlIndicator, setDdlIndicator] = useState()
    const [currentId, setCurrentId] = useState('')

    function handleClick(id) {
        if (id === currentId) {
            setDdlContainer({...ddlContainer, maxHeight: 0})
            setCurrentId('')
        } else {
            setDdlContainer({...ddlContainer, maxHeight: 400})
            setCurrentId(id)
        }
    }

    function handleMouseLeave() {
        setDdlContainer({...ddlContainer, maxHeight: 0})
        setCurrentId('')
    }
    
    return (
        <span onMouseLeave={handleMouseLeave} style={optionsContainer}>
            <span style={optionsMenuContainer}>
                <div onClick={() => handleClick('nav')}><Navigation/></div>
                <div onClick={() => handleClick('alert')}><Alerts/></div>
                <div onClick={() => handleClick('setting')}><Settings/></div>
            </span>

            <div style={ddlContainer}/>
        </span>
    )
}
