let container = {
    zIndex: 5,
    display: 'flex',
    width: '25%',
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let menuContainer = {
    display: 'flex',
    width: 304,
    marginLeft: 'auto'
}

let ddlContainer = {
    width: 304,
    height: 400,
    maxHeight: 0,
    marginLeft: 'auto',
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px',
    transition: 'all 0.5s'
}

let ddlIndicator = {
    position: 'relative',
    top: 3,
    width: 80,
    height: 1,
    backgroundColor: 'white',
    opacity: 0,
    transition: 'opacity 0.5s, left 0.3s'
}

let contentOpen = {
    opacity: 1,
    // transition: 'all 0.3s',
    visibility: 'visible'
}

let contentClosed = {
    opacity: 0,
    // transition: 'all 0.3s',
    visibility: 'hidden'
}

export {
    container,
    menuContainer,
    ddlContainer,
    ddlIndicator,
    contentOpen,
    contentClosed
}
