import { useRef, useState } from "react"
import SimpleBarReact from 'simplebar-react';

let itemContainer = {
    display: 'flex',
    height: 80, 
    width: '48%',
    minWidth: 600,
    backgroundColor: '#e4f7ff',
    marginBottom: 8,
    borderRadius: '10px 5px 5px 5px',
    cursor: 'pointer',
    transition: 'all 0.5s'
}

let mainInfoContainer = {
    display: 'flex',
    width: '15%',
    flexDirection: 'column'
}

let subInfoContainer = {
    display: 'flex',
    width: '65%',
    flexDirection: 'column'
}

let timeContainer = {
    width: '20%',
    textAlign: 'right',
    margin: '18px 20px 0 0'
}

let isd = {
    width: '100%',
    color: '#e4f7ff',
    textAlign: 'center',
    lineHeight: 2.2,
    height: 40,
    fontSize: 18,
    backgroundColor: '#063b50',
    borderRadius: '5px 0 0 0'
}

let status = {
    fontSize: 20,
    margin: '5px auto',
    textTransform: 'capitalize'
}

let subject = {
    fontSize: 18,
    margin: '7px 0 0 10px',
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis'
}

let body = {
    fontSize: 12,
    opacity: 0.5,
    height: 20,
    margin: '23px 0 0 10px',
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis',
    transition: 'all 0.5s'
}

export default function Ticket({data}) {
    const [bodyHTML, setBodyHTML] = useState(formatLongBody(data.body))
    const ticketRef = useRef(null)
    const bodyRef = useRef(null)

    function renderItems() {
        return Object.keys(data.items).map((item, i) => (
            <div key={`Ti${i}`}>{item} {data.items[item]}</div>
        ))
    }

    function handleClick(ticketRef, bodyRef) {
        if (ticketRef.current.style.height === '80px') {
            ticketRef.current.style.height = '300px';
            bodyRef.current.style.height = '230px'
            // setBodyHTML(formatLongBody(data.body))
        } else {
            ticketRef.current.style.height = '80px';
            bodyRef.current.style.height = '20px'
            // setBodyHTML(formatShortBody(data.body))
        }
    }

    function handleMouseEvent(e) {
        e.backgroundColor === 'rgb(228, 247, 255)'
      ? e.backgroundColor = '#bac6da'
      : e.backgroundColor = '#e4f7ff'
    }
    
    function formatShortBody(body) {
        return body.includes('From') ? body.split('From')[0] : body
    }

    function formatLongBody(body) {
        function format(item) {
            if (item === '') return <div style={{height: 17}}></div>
            if (item.includes('From: ')) {
                return (
                    <div>
                        <div style={{height: 1, width: '100%', backgroundColor: 'black'}}/>
                        <div style={{marginTop: 10, whiteSpace: 'initial'}}>{item}</div>
                    </div>
                )
            } else return <div style={{whiteSpace: 'initial'}}>{item}</div>
        }

        function removeTrailingSpace() {
            let cleanBody = body.split('\n').reverse()

            for (let i = 0; i < cleanBody.length; i++) {
                if (!cleanBody[i]) cleanBody.splice(i, 1)
                else break
            }

            return cleanBody.reverse().map((item, i) => (
                <div>{format(item)}</div>
            ))
        }

        return (
            <SimpleBarReact style={{display: 'flex', flexDirection: 'column', width: '100%', height: 230}}>
                {removeTrailingSpace()}
            </SimpleBarReact>
        )
    }

    function formatDate(date) {
        const months = {
            Jan: '01',
            Feb: '02',
            Mar: '03',
            Apr: '04',
            May: '05',
            Jun: '06',
            Jul: '07',
            Aug: '08',
            Sep: '09',
            Oct: '10',
            Nov: '11',
            Dec: '12',
          }

        date = (date.split(' ')[0]).split('-')
        return `${Object.keys(months).filter(i => months[i] === date[1])[0]} ${date[2]}, ${date[0]}`
    }

    function formatTime(date) {
        return `${date.split(' ')[1]} ${date.split(' ')[2]}`
    }

    return (
        <span style={itemContainer}
              ref={ticketRef}
              onMouseEnter={() => handleMouseEvent(ticketRef.current.style)}
              onMouseLeave={() => handleMouseEvent(ticketRef.current.style)}
              onClick={() => {handleClick(ticketRef, bodyRef)}}>

            <div style={mainInfoContainer}>
                <div style={isd}>{data.isd}</div>
                <div style={status}>{data.status}</div>
            </div>

            <div style={subInfoContainer}>
                <div style={subject}>{data.subject}</div>
                <div ref={bodyRef}style={body}>{bodyHTML}</div>
            </div>

            <div style={timeContainer}>
                <div>{formatTime(data.date)}</div>
                <div>{formatDate(data.date)}</div>
            </div>
        </span>
    )
}
