import {React, useState} from 'react';
import * as style from "../css/searchList";
import {phones} from "../data/phoneList";
import ListItem from "./listItem";
import IndexMenu from "./indexMenu";

let input = [];
let string = "";
let searchData = [];
let relevantSearch = "mac";
let filteredSearch = [];
let infoType = [];

//Filters out redundant keys from user input, and changes string to lowercase.
const filterInput = (e) => {
  if (e.key === "Shift") {
    return;
  }
  if (e.key === "Backspace") {
    input.pop()
    string = input.join("")
  }
  else {
    input.push(e.key.toLowerCase())
    string = input.join("")
  };

  console.log(input)
};

//Retrieves and filters data from phone json files and creates an array with relevant information.
const getPhoneData = () => {
  let i = 0
  let container = [];
  phones.forEach(phone => {
    container = require(`../data/phones/${phone}.json`)
    searchData.push([
      {
        key: i,
        type: "mac",
        data: container.resources[0].data.name.toUpperCase()
      },
      {
        key: i,
        type: "user",
        data: container.resources[0].data.lines.line[0].displayAscii
      },
      {
        key: i,
        type: "line",
        data: container.resources[0].data.lines.line[0].dirn.pattern
      },
      {
        key: i,
        type: "mask",
        data: container.resources[0].data.lines.line[0].e164Mask
      },
      {
        key: i,
        type: "ip",
        data: container.resources[0].data.ip_address
      },
      {
        key: i,
        type: "location",
        data: container.resources[0].meta.hierarchy.split("_").pop().toLowerCase()
      }
    ])
  })
  i++;
  //Replaces undefined and null values with empty string for searchData
  searchData.forEach(e => {if (!e[1].data) e[1].data = ""})
};

//Removes duplicates and returns a list of devices that include any data value corresponding with user input string.
const returnSearch = () => {
  let unfilteredSearch = []

  searchData.forEach(profile => {
    profile.forEach(e => {
      if (!e.data) e.data = "";
      if (e.data.toLowerCase().includes(string)) {
        infoType.push(e.type)
        unfilteredSearch.push(profile)
      }
    })
  })
  filteredSearch = [...new Set(unfilteredSearch)];
};

//Returns the most relevant search type and passes it to the IndexMenu prop to change highlighting for index.
const getCount = () => {
  let counts = [];
  infoType.forEach(e => {
    counts[e] = (counts[e] || 0) + 1;
  });
  relevantSearch = Object.keys(counts)[0];
  infoType = [];
};


getPhoneData()

export default function SearchList() {
  const [device, setDevice] = useState(searchData)
  const [input, setInput] = useState("")

  //Renders a list with all devices returned to filteredSearch and passed to device state.
  const renderPhoneList = () => {
    return device.map((item, i) => (
      <ListItem 
        key={`ID${i}`}
        mac={device[i][0].data}
        user={device[i][1].data.toLowerCase()}
        line={device[i][2].data}
        mask={device[i][3].data}
        ip={device[i][4].data}
        location={device[i][5].data.toLowerCase()}
      />
    ))
  };

  const handleChange = (e) => {
    setInput(e.target.value)
    console.log(input)
  }

  const handleSubmit = (e) => {
    console.log(input)
    e.preventDefault()
  }

  return (
    <div>
      <input type="text" value={input} onChange={() => handleChange}/>
      <input type="submit"/>
      <IndexMenu relevantSearch={relevantSearch}/>
      {/* <div style={style.listContainerStyle}>{renderPhoneList()}</div> */}
    </div>
  )
};



