import React, {useEffect, useRef, useState} from 'react';
import * as style from "../css/light/pageIndex";
import getOverallTotal from './getOverallTotal';

export default function Pages(props) {
  const [total, setTotal] = useState(getOverallTotal(props))
  const [pageStyle, setPageStyle] = useState(style.pageNumber)
  const leftTab = useRef(null);
  const rightTab = useRef(null);

  useEffect(() => {
    setTimeout(() => {
      setTotal(getOverallTotal(props))
    }, 0.01)
  })

  const renderPageList = () => {
    let pages = []
    for (let i = 2; i < total; i++) {
      pages.push(i)
    }

    return pages.map((i, item) => (
        <div key={`Page${i}`} id={i} style={pageStyle}>{i}</div>
      )
    )
  }

  return (
    <div style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          <div id={1} style={style.firstPage}>1</div>
          <div ref={leftTab} style={style.leftTab}>|</div>
          <div style={{display: "flex", maxWidth: "150px", overflow: "hidden"}}>
            {renderPageList()}
          </div>
          <div ref={rightTab} style={style.rightTab}>|</div>
          <div id={total} style={style.lastPage}>{total}</div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
