import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, ret=['Device', 'User', 'DID', 'borderWidth']) {
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? data['display']['case'][type.toLowerCase()] : data['display']['case']['borderWidth'];
      
      bool.forEach((value, i) => {
        console.log(Object.keys(data['display'][Boolean(value)])[i])
        let pointToTarget = data['display'][Object.keys(data['display'][Boolean(value)])[i]]
        let pointToStyle = data['display'][Boolean(value)][Object.keys(data['display'][Boolean(value)])[i]]
        // console.log(data['display'])
        pointToTarget['highlighted'] = Boolean(value);
        console.log(pointToStyle)
        data['display']['style'] = pointToStyle;
      });
    };
  });

  console.log(data)

  return data;
};
