import { useEffect, useState } from "react"
import callTickets from "./api/callTickets"
import Ticket from './ticket/ticket'
import Options from '../optionsddl/options'
import SimpleBarReact from 'simplebar-react';


let container = {
    width: 119,
    height: 27,
    backgroundColor: '#063b50',
    color: 'white',
    textAlign: 'center',
    paddingTop: 3,
    marginRight: 2,
    fontWeight: 'bold'
}

let input = {
    width: 793,
    height: 23,
    fontSize: 17,
    margin: '8px 0 0 10px',
    backgroundColor: "#e4f7ff"
}

function IndexButton({title}) {
    return (
        <div style={container}>
            {title}
        </div>
    )
}


function Input() {
    return (
        <input style={input}></input>
    )
}


function Focused({focused}) {
    if (focused) {
        return (
            <div style={{width: 600, height: 780, backgroundColor: 'blue', margin: '10px 0 0 10px'}}></div>
        )
    } else {
        return <div></div>
    }

}


export default function LdTickets() {
    const [tickets, setTickets] = useState([])
    const [focused, setFocused] = useState()

    useEffect(() => {
        const getData = async () => {
            let res = await callTickets()
            setTickets(res.sort((a, b) => b.isd - a.isd))
        }

        getData()
    }, [])

    function renderTickets() {
        return tickets.map((item, i) => (
            <Ticket key={`T${i}`}
                    ticket={item}
                    setFocused={setFocused}
                    data={item}/>
        ))
    }

    function renderIndex() {
        return (
            <span style={{display: 'flex'}}>
                <IndexButton title='Open'/>
                <IndexButton title='Closed'/>
            </span>
        )
    }

    return (
        <span>
            <div style={{display: 'flex', marginRight: 10}}>
                <div style={{margin: '10px 0 0 10px'}}>{renderIndex()}</div>
                <Options/>
            </div>
            <Input/>
            <div style={{display: 'flex', width: '60%'}}>
                <SimpleBarReact style={{height: 800, width: '72%'}}>
                    <div>{renderTickets()}</div>
                </SimpleBarReact>
                {/* <Focused focused={focused}/> */}
            </div>
        </span>
    )
}
