// import getSearch from "./getSearch"
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";

export default function returnSearch(string, listToRender) {
  let temp = {}

  listToRender.forEach(listItem => {
    switch(listItem) {
      case "Device":
        temp[listItem.toLowerCase()] = getSearch(getDeviceData, string.target.value.toLowerCase()); 
        break
      case "User": 
        temp[listItem.toLowerCase()] = getSearch(getUserData, string.target.value.toLowerCase()); 
        break
      default: break
    } 

  })
  return temp
}
