import { useEffect, useState } from "react"
import * as style from "../../css/light/focusedList"

export default function Focused(props) {
  const [focused, setFocused] = useState(props.focused)

  useEffect(() => {
    setFocused([...focused, props.focused])
  }, [props.focused])

  const renderFocusedList = () => { 
    return props.focused.map((item, i) => (
      <div>
        <div style={style.focusedItemTitleContainer} key={`F${i}`}>
        <div style={{width: "25%"}}></div>  
          <div style={style.focusedItemTitle}>
            {/* {item[0].resources[0].data.name} */}
          </div>
          <div>
            <div onClick={(e) => {
              // setFocused(() => {
              //   focused.filter(word => word !== item)
              //   console.log(focused.filter(word => word !== item))
              // })
              // focused.forEach((e) => {
              //   console.log(e[0].resources[0].data.name)
              // })
            }} style={style.focusClose}>+</div>
          </div>
        </div>
        <div style={style.focusedItemDD}></div>
      </div>
    ))
  }

  return (
    <div style={style.container}>{renderFocusedList()}</div>
  )
}
