import { useState } from 'react'
import Alerts from './alerts/alerts'
import Navigation from './navigation/navigation'
import Settings from './settings/settings'

import ReactCSSTransitionGroup from 'react-transition-group'

let optionsContainer = {
    display: 'flex',
    width: 300,
    height: 30,
    margin: "10px 0 0 10px",
    marginLeft: 'auto',
    flexWrap: 'wrap'
}

let optionsMenuContainer = {
    display: 'flex',
    width: 299
}

let optionsDdlStyle = {
    width: 300,
    maxHeight: 400,
    height: 0,
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px'
}

export default function Options() {
    const [optionsDdl, setOptionsDdl] = useState(optionsDdlStyle)

    function handleClick() {
        return (
            <ReactCSSTransitionGroup>
                transitionName="test"
                transitionAppear={true}
            </ReactCSSTransitionGroup>
        )
        // setOptionsDdl({...optionsDdl, transition: 'max-height 0.3s ease-in'})
        // console.log(optionsDdl)
    }

    function handleMouseLeave() {
        
        setOptionsDdl({...optionsDdl, height: 0})
    }
    
    return (
        <span style={optionsContainer}>
            <span style={optionsMenuContainer}>
                <div onClick={handleClick}><Navigation/></div>
                <div onClick={handleClick}><Alerts/></div>
                <div onClick={handleClick}><Settings/></div>
            </span>

            {/* <div onMouseEnter={handleClick} style={optionsDdl}/> */}
        </span>
    )
}
