import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total

  const [pageStyle, setPageStyle] = useState(style.pageNumber)
  const [leftTab, setLeftTab] = useState(style.leftTab)
  const [rightTab, setRightTab] = useState(style.rightTab)
  const [visible, setVisible] = useState([10, 11, 12, 13, 14])

  const handleClick = (e) => {
    setVisible([parseInt(e.target.id)-2, parseInt(e.target.id)-1, e.target.id, parseInt(e.target.id)+1, parseInt(e.target.id)+2,])
  }

  const handleFirstPageClick = (e) => {
    setVisible([2,3,4,5,6])
    style.leftTab.visibility = "hidden"
  }

  const renderPageList = () => {
    return visible.map((i, item) => (
        <div key={`Page${i}`} id={i} onClick={e => handleClick(e)} style={pageStyle}>{i}</div>
      )
    )
  }

  return (
    <div style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          <div id={1} onClick={e => handleFirstPageClick(e)} style={style.firstPage}>{`${total.device - (total.device - 1)}`}</div>
          <div style={leftTab}>|</div>
          {renderPageList()}
          <div style={rightTab}>|</div>
          <div style={style.lastPage}>{`${total.device}`}</div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
