import { useRef} from "react";
import * as style from "../../css/light/focusedList";
import FocusedTitle from "./focusedTitle"
import FocusedHistory from "./focusedHistory";
import FocusedListItem from "./focusedListItem"

let focusedDataTitle = {
  backgroundColor: "#4382BC",
  textAlign: "center",
}

export default function Focused({focused, setFocused}) {
  if (!focused[0]) return <div></div>
  let focusedItem = require(`../../data/${focused[1]}/${focused[0]}.json`)

  function FocusedData() {
    return (
      <div style={{overflow: "hidden", display: "flex"}}>                                     {/*height: 398px*/}
        <div style={{width: "97%", margin: "12px auto 0px auto", border: "1px solid black", backgroundColor: "white"}}>
          <div style={focusedDataTitle}>Current Profile</div>
          <div style={{backgroundColor: "white"}}>
            <FocusedList/>
          </div>
        </div>  
      </div>
    )
  }

  function FocusedList() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div 
          onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
          onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}
          style={{fontSize: "11px", width: "100%"}}>
        <FocusedListItem item={item} focusedItem={focusedItem}/>
      </div>
    ))
  }

  if (focusedItem.resources[0].data.associatedDevices) {
    return (
      <div style={style.focusedContainer}>
        <FocusedTitle setFocused={setFocused} focusedItem={focusedItem}/>
        <FocusedData/>
        <FocusedHistory focusedItem={focusedItem}/>
      </div>
    )
  }
}
