let itemContainer = {
    display: 'flex',
    height: 80, 
    width: 800, 
    backgroundColor: '#e4f7ff',
    margin: '8px 0 0 10px',
    borderRadius: '10px 5px 5px 10px',
    textTransform: 'capitalize',
}

let mainInfoContainer = {
    display: 'flex',
    width: '15%',
    flexDirection: 'column'
}

let subInfoContainer = {
    display: 'flex',
    width: '65%',
    flexDirection: 'column'
}

let timeContainer = {
    width: '20%'
}

let isd = {
    width: '100%',
    color: '#e4f7ff',
    textAlign: 'center',
    lineHeight: 2.2,
    height: 40,
    fontSize: 18,
    backgroundColor: '#063b50',
    borderRadius: '5px 0 0 0'
}

let status = {
    fontSize: 20,
    margin: 'auto'
}

let subject = {
    fontSize: 18,
    margin: '7px 0 0 10px',
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis'
}

let body = {
    fontSize: 12,
    opacity: 0.5,
    margin: '23px 0 0 10px',
    overflow: 'hidden',
    whiteSpace: 'nowrap',
    textOverflow: 'ellipsis'
}

export default function Ticket({data}) {
    function renderItems() {
        return Object.keys(data.items).map((item, i) => (
            <div key={`Ti${i}`}>{item} {data.items[item]}</div>
        ))
    }

    function formatShortBody(body) {
        if (body.includes('From')) {
            return body.split('From')[0]
        } else {
            return body
        }
    }

    function formatDate(date) {
        const months = {
            Jan: '01',
            Feb: '02',
            Mar: '03',
            Apr: '04',
            May: '05',
            Jun: '06',
            Jul: '07',
            Aug: '08',
            Sep: '09',
            Oct: '10',
            Nov: '11',
            Dec: '12',
          }

        date = (date.split(' ')[0]).split('-')
        return `${Object.keys(months).filter(i => months[i] === date[1])[0]} ${date[2]}, ${date[0]}`
    }

    return (
        <span style={itemContainer}>
            <div style={mainInfoContainer}>
                <div style={isd}>{data.isd}</div>
                <div style={status}>{data.status}</div>
            </div>

            <div style={subInfoContainer}>
                <div style={subject}>{data.subject}</div>
                <div style={body}>{formatShortBody(data.body)}</div>
            </div>

            <div style={timeContainer}>{formatDate(data.date)}</div>

            {/* <div style={{width: '85%', display: 'flex', justifyContent: 'space-between'}}>
                <div style={{marginLeft: 5}}>
                    <div style={{display: 'flex', justifyContent: 'space-between'}}>
                        <div style={{width: 200}}>{data.date}</div>
                        <div>{data.status}</div>

                    </div>

                    <div style={{width: 250, backgroundColor: 'black', height:1}}></div>

                    <div style={{overflow: 'ellipsis'}}>Subject: {data.subject}</div>
                </div> */}

            {/* </div> */}
        </span>
    )
}
