import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  let pageNumber = {
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }
  let pageNumberHighlight = {
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "white"
  }
  const [pageStyle, setPageStyle] = useState(pageNumber)
  const [visible, setVisible] = useState([1, 2, 3, 4, 5, total.device])

  const handleClick = (e) => {
    if (e.target.id < 3) {
      setVisible([1, 2, 3, 4, 5, total.device])
    }
    else if (parseInt(e.target.id) >= total.device -2) {
      setVisible([total.device-5, total.device-4, total.device-3, total.device-2, total.device-1, total.device.splice(3)])
      e.target.value = total.device
    }
    else {
      setVisible([parseInt(e.target.id)-2, parseInt(e.target.id)-1, e.target.id, parseInt(e.target.id)+1, parseInt(e.target.id)+2, total.device])
    }
  }

  const renderPageList = () => {
    let pages = []

    for (let i in visible) {
      if (parseInt(i)+1 === visible.length) {
        pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageStyle}>{`...${visible[i]}`}</div>)
      }
      else {
        pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageStyle}>{visible[i]}</div>)
      }
    }
    return pages
  }


  return (
    <div style={{display: "flex", overflow: "hidden "}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          {renderPageList()}
        </div>
      <div id="test" test="test" onClick={e => handleClick(e)} style={style.rightArrow}/>
    </div>
  )
}
