import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  let pageNumber = {
    width: "15px",
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }

  let lastPage = {
    width: "15px",
    marginRight: "10px",
    marginLeft: "-5px",
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }

  let pageNumberHighlight = {
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "white"
  }
  const [pageStyle, setPageStyle] = useState(pageNumber)
  const [visible, setVisible] = useState([1, 2, 3, 4, 5,])

  const handleClick = (e) => {
  }

  const renderPageList = () => {
    let pages = []

    for (let i in visible) {
      pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageStyle}>{visible[i]}</div>)
    }
    return pages
  }

  return (
    <div style={{display: "flex", overflow: "hidden "}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          {renderPageList()}
          <div style={lastPage}>
            {`...${total.device}`}
          </div>
        </div>
      <div onClick={e => handleClick(e)} style={style.rightArrow}/>
    </div>
  )
}
