import {useRef, React, useState} from 'react';
import * as style from "../css/searchList";
import ListDeviceItem from "./listDeviceItem";
import ListUserItem from "./listUserItem";
import IndexMenu from "./indexMenu";
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";

let string = "";
let searchDevice = getDeviceData;
let searchUser = getUserData;
let relevantSearch = "mac";
let filteredDeviceSearch = [""];
let filteredUserSearch = [""];

export default function SearchList() {
  const [device, setDevice] = useState(searchDevice);
  const [user, setUser] = useState(searchUser);
  const [scroll, setScroll] = useState({scrollTop: 0})
  const [deviceDisplay, setDeviceDisplay] = useState(true);
  const [userDisplay, setUserDisplay] = useState(true);
  const [searchTypeDeviceStyle, setSearchTypeDeviceStyle] = useState(style.searchTypeDeviceStyle);
  const [searchTypeUserStyle, setSearchTypeUserStyle] = useState(style.searchTypeUserStyle);
  const scrollRef = useRef({scrollTop: 0})
  
  //Removes duplicates and returns a list of devices that include any data value corresponding with user input string.
  const returnSearch = () => {
    let infoType = [];
    let unfilteredDeviceSearch = [];
    let unfilteredUserSearch = [];
    let i = 0
  
    searchDevice.forEach(profile => {
      profile.forEach(e => {
        if (e.data.toString().toLowerCase().includes(string)) {
          deviceInfoType.push(e.type);
          unfilteredDeviceSearch.push(profile);
        }
      });
    });
    filteredDeviceSearch = [...new Set(unfilteredDeviceSearch)];
  
    searchUser.forEach(profile => {
      profile.forEach(e => {
        if (e.data.toString().toLowerCase().includes(string)) {
          infoType.push(e.type);
          unfilteredUserSearch.push(profile);
        }
      })
    })
    filteredUserSearch = [...new Set(unfilteredUserSearch)];
  
    //Highlights most relavent searchindex.
    let deviceCounts = [];
    deviceInfoType.forEach(e => {
      deviceCounts[e] = (deviceCounts[e] || 0) + 1;
    });
    deviceRelevantSearch = Object.keys(deviceCounts)[0];
    deviceInfoType = [];
  };

  const handleInputChange = e => {
    string = e.target.value;
    returnSearch();
    setDevice(filteredDeviceSearch);
    setUser(filteredUserSearch);
  }

  const handleSearchTypeChange = e => {
    if (e.target.textContent === "Device" && !e.target.style["borderWidth"]) {
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyleHighlight)
      setSearchTypeUserStyle(style.searchTypeUserStyle)
      setDeviceDisplay(true)
      setUserDisplay(false)
      return
    }

    if (e.target.textContent === "User" && !e.target.style["borderWidth"]) {  
      setSearchTypeUserStyle(style.searchTypeUserStyleHighlight)
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyle)
      setDeviceDisplay(false)
      setUserDisplay(true)
      return
    }

    if (e.target.style["borderWidth"]) {
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyle)
      setSearchTypeUserStyle(style.searchTypeUserStyle)
      setDeviceDisplay(true)
      setUserDisplay(true)
      return
    }
  }

  const handleScroll = e => {
    setScroll({scrollTop: scrollRef.current.scrollTop})
  }

  //Renders a list with all devices returned to filteredSearch and passed to device state.
  const renderPhoneList = () => {
    let container = []
    let i = 0
    while (i < 10 ) {
      if (!device[i]) break
      container.push(device[i])
      i++
    }
    if (deviceDisplay) {
      return container.map((item, i) => (
        <ListDeviceItem
          key={`ID${i}D`}
          mac={item[0].data}
          user={item[1].data.toLowerCase()}
          line={item[2].data}
          mask={item[3].data}
          ip={item[4].data}
          location={item[5].data.toLowerCase()}
        />
      ));
    }
  };

  const renderUserList = () => {
    let container = []
    let i = 0
    while (i < 10) {
      if (!user[i]) break
      container.push(user[i])
      i++;
    }
    if (userDisplay) {
      return container.map((item, i) => (
        <ListUserItem
          key={`ID${i}U`}
          id={item[0].data}
          name={item[1].data}
          primary={item[2].data}
          department={item[3].data}
          device={item[4].data}
          location={item[5].data}
        />
      ));
    }
  };

  const renderDeviceIndexMenu = () => {
    if (deviceDisplay) {
      if (filteredDeviceSearch.length === 0) return <div></div>
      return <IndexMenu 
        index1={["MAC Address", "mac"]} 
        index2={["User", "user"]} 
        index3={["Primary Line", "line"]} 
        index4={["Line Mask", "mask"]} 
        index5={["IP Address", "ip"]}
        index6={["Location", "location"]}
      />
    }
  }

  const renderUserIndexMenu = () => {
    if (userDisplay) {
      if (filteredUserSearch.length === 0) return <div></div>
      return <IndexMenu 
        index1={["User ID", "id"]} 
        index2={["Name", "name"]}
        index3={["Primary Line", "primary"]} 
        index4={["Department", "department"]}
        index5={["Device", "device"]}
        index6={["Location", "location"]}
      />
    }
  }

  return (
    <div style={{minWidth: "59.5vw"}}>
      <div style={{display: "flex"}}>
        <div onClick={e => handleSearchTypeChange(e)} style={searchTypeDeviceStyle}>Device</div>
        <div onClick={e => handleSearchTypeChange(e)} style={searchTypeUserStyle}>User</div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div >
        {/* <IndexMenu relevantSearch={relevantSearch}/> */}
        <div ref={scrollRef} onScroll={e => handleScroll(e)} style={style.listContainerStyle}>
          {renderDeviceIndexMenu()}
          {renderPhoneList()}
          {renderUserIndexMenu()}
          {renderUserList()}
        </div>
      </div>
    </div>
  );
};
