import getPageIndexCase from "./getPageIndexCase"
import getOverallTotal from "./getOverallTotal"
import * as pageStyle from "../../css/light/pageIndex"

export default function PageIndex(props) {
  let pages = []
  let total = getOverallTotal(props.data)
  let pageRef = []

  try {
    let pageIndexResults = getPageIndexCase(props.data, total, props.firstNum, props.lastNum, props.leftTab, props.rightTab)
    props.firstNum.current.innerHTML = pageIndexResults[0].innerHTML
    props.lastNum.current.innerHTML = pageIndexResults[1].innerHTML
    props.lastNum.current.style.display = pageIndexResults[1].style.display
    props.leftTab.current.style.display = pageIndexResults[2].style.display
    props.rightTab.current.style.display = pageIndexResults[3].style.display
  }
  catch {/*first render*/}

  for (let i = 2; i < total; i++) pages.push(i)

  return (
    <div style={{display: "flex"}}>  
      <div style={pageStyle.leftArrow}/>
        <div style={{display: "flex"}}>
          <div style={pageStyle.firstPage}>1</div>
          <div style={pageStyle.leftTab}>|</div>
          <div style={{display: "flex", maxWidth: "192px", overflow: "hidden"}}>
            <div style={{display: "flex"}}>
              {pages.map((i, item) => (
                <div ref={pageRef[i]} onClick={(e) => {console.log(this.ref)}} key={`Page${i}`} id={i} style={pageStyle.pageNumber}>{i}</div>
                ))
              }
            </div>
          </div>
          <div style={pageStyle.rightTab}>|</div>
          <div style={pageStyle.lastPage}>{total}</div>
        </div>
      <div style={pageStyle.rightArrow}/>
    </div>
  )
}
