function renderObject(selector, value, indent, run=1) {
  if (value === null) return (
    <div style={{display: "flex"}}>
      <div style={{width: "100%", direction: "rtl"}}>{selector}</div>
      <div style={{width: "50%"}}>undefined</div>
    </div>
  )

  if (typeof(value) === "object") {
    return (
      <div>
        <div style={{display: "flex"}}>
          <div>{selector}</div>
          <div style={arrow}></div>
        </div>
        <div style={{textIndent: `${indent+15}px`}}>
          {function() {
            return Object.keys(value).map((new_selector) => (
              renderObject(new_selector, value[new_selector], indent+15, run+1)
            ))
          }()}
        </div>
      </div>
    )
  }
  else return (
    <div style={{display: "flex"}}>
      <div style={{width: "100%", direction: "rtl"}}>{selector}</div>
      <div style={{width: "50%"}}>{value}</div>
    </div>
