import profiles from "../data/profileList.json";
import getHcsLink from "./getHcsLink";

export default function getDeviceData() {
  let profileList = [];

  profiles['devices'].forEach((device, i) => {
    const data = require(`../data/devices/${device}.json`);

    profileList.push([
      {
        type: "mac",
        index: "MAC Address",
        data: function() {
          try {
            return !data.data.name.toUpperCase() 
            ? ""
            : data.data.name.toUpperCase() 
          } 
          catch {return ""}
        }()
      },
      {
        type: "product",
        index: "Product",
        data: function() {
          try {
            return !data.data.product
            ? ""
            : data.data.product
          } 
          catch {return ""}
        }()
      },
      {
        type: "user",
        index: "User ID",
        data: function() {
          try {
            return !data.data.ownerUserName
            ? ""
            : data.data.ownerUserName
          } 
          catch {return ""}
        }()
      },
      {
        type: "description",
        index: "Description",
        data: function() {
          try {
            return !data.data.description
            ? ""
            : data.data.description
          } 
          catch {return ""}
        }()
      },
      {
        type: "primary",
        index: "Primary Line",
        data: function() {
          try {
            return !data.data.lines.line[0].dirn.pattern
            ? ""
            : data.data.lines.line[0].dirn.pattern
          } 
          catch {return ""}
        }()
      },
      {
        type: "mask",
        index: "E164 Mask",
        data: function() {
          try {
            return !data.data.lines.line[0].e164Mask
            ? ""
            : data.data.lines.line[0].e164Mask
          } 
          catch {return ""}
        }()
      },
      {
        type: "location",
        index: "Location",
        data: function() {
          try {
            return !data.meta.hierarchy.split("_").pop()
            ? ""
            : data.meta.hierarchy.split("_").pop()
          } 
          catch {return ""}
        }()
      },
      {
        type: "link",
        index: "Link",
        data: getHcsLink(data.data.name, "devices")
      }
    ]);
  });

  return profileList;
};
