import {React, useEffect, useState, useRef} from 'react';
import * as __data from "./searchListData"
import * as style from "../../css/light/searchList";
import * as focusStyle from "../../css/light/focusedList";
import Pages from "../pages";
import Focused from '../focused/focused';
import renderIndexMenu from './renderIndexMenu';
import getItem from './getItem';
import getTotalCount from './getTotalCount';
import renderList from './renderList';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import getSearch from './getSearch';
import getFilteredSearch from './getFilteredSearch';
import handleSearchTypeChange from "./handleSearchTypeChange";

let filteredSearch = {
  "device": {"results": getDeviceData, "relevant": "mac"}, 
  "user": {"results": getUserData, "relevant": "id"},
  "did": {"results": getDidData, "relevant": "did"}
}

let testFocused = []

export default function SearchList() {
  const [data, setData] = useState(__data['default'])
  const [item, setItem] = useState(getItem(filteredSearch))
  const [focused, setFocused] = useState([])
  const [pageNumber, setPageNumber] = useState(1)
  const [tempItem, setTempItem] = useState("")

  useEffect(() => {
    // setData(getTotalCount(filteredSearch, data))
  }, [data])

  const handleInputChange = string => {
    let search = getSearch(getDeviceData, getUserData, getDidData, string, filteredSearch)
    filteredSearch = getFilteredSearch(search)
    setItem(getItem(filteredSearch))
    setData(getTotalCount(filteredSearch, data))
  }

  const getSearchTypeChange = (e) => {
    let results = handleSearchTypeChange(e, data)
    setData({...results})
  }

  const handleFocus = (e, type, id) => {
    if (e.target.style.cursor !== "pointer") return
    for (let i in focused) if (e.target.innerHTML === focused[i][0].resources[0].data[id]) return
    testFocused = [[require(`../../data/${type}/${e.nativeEvent.path[1].innerText.split('\n')[0]}.json`), id, type], ...testFocused]
    setFocused(testFocused)
    return 
  }

  const handleFocusChange = item => {
    let temp = testFocused
    temp.splice(temp.indexOf(item), 1)
    setFocused(temp)
  }

  const renderFocusedLines = (tempLine) => {
    return tempLine.map((item, i) => (
      <div>{item}</div>
    ))
  }

  const focusedDDInfo = (data, type) => {
    switch(type) {
      case "users":
        let tempUser = []
        let tempLine = []
        data.resources[0].data.Phone.forEach((e) => {
          tempUser.push(e.name)
          console.log(e.lines)
          e.lines.line.forEach((i) => {
            tempLine.push(i.label)
          })
        })

        return tempUser.map((item, i) => (
          <div style={{display: "inline-block", position: "relative"}}>
            <div style={{height: "220px", width: "32vw", textAlign: "center", border: "1px solid #4382BC", margin: "3px 6px 3px 0px"}}>{item}</div>
            {/* <div>{renderFocusedLines(tempLine)}</div> */}
          </div>
        ))

      case "phones":
    }
  }
  //
  const renderFocusedList = () => {
    let margin = focused.length === 1 ? "0px 0px 0px 0px" : "0px 0px 5px 0px"
    let focusedItemDD = {...focusStyle.focusedItemDD, margin}

    return focused.map((item, i) => (
      <div key={i}>
        <div style={focusStyle.focusedItemTitleContainer} key={`F${i}`}>
        <div style={{width: "25%"}}></div>  
          <div style={focusStyle.focusedItemTitle}>{item[0].resources[0].data[item[1]]}</div>
          <div>
            <div onClick={() => {
              setTempItem(item)
              handleFocusChange(item)
            }} 
            style={focusStyle.focusClose}>+</div>
          </div>
        </div>
        <div style={focusedItemDD}>
          <div style={{marginLeft: "9px", textAlign: "center", fontSize: "17px", fontWeight: "bold", paddingTop: "9px"}}>Associated Devices</div>
          <div style={{margin: "3px 0px 2px 8px", height: "1px", width: "32.1vw", backgroundColor: "#4382BC"}}></div>
          <div style={{height: "300px", overflowY: "scroll", overflowX: "hidden", marginLeft: "8px", display: "block"}}>{focusedDDInfo(item[0], item[2])}</div>
          <div style={{position: "absolute", top: "509px"}}>
            <div style={{marginLeft: "9px", textAlign: "center", fontSize: "17px", fontWeight: "bold"}}>User Profile History</div>
            <div style={{margin: "3px 0px 6px 8px", height: "1px", width: "32.1vw", backgroundColor: "#4382BC"}}></div>
            <div style={{backgroundColor: "white", marginLeft: "8px", marginTop: "3px", width: "32vw", height: "218px", border: "1px solid black"}}></div>
          </div>
        </div>
      </div>
    ))
  }

  return (
    <div>
      <div style={{maxWidth: "63.5vw"}}>
        <div style={{display: "flex", marginBottom: "5px"}}>
          <div onClick={e => getSearchTypeChange(e)} style={style[data['display']['style']['device']]}>Device</div>
          <div onClick={e => getSearchTypeChange(e)} style={style[data['display']['style']['user']]}>User</div>
          <div onClick={e => getSearchTypeChange(e)} style={style[data['display']['style']['did']]}>DID</div>
          <div onClick={e => getSearchTypeChange(e)} style={style[data['display']['style']['line']]}>Line</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div>
        <div style={focusStyle.container}>{renderFocusedList()}</div>
        <div style={style.listContainerStyle}>
          <div>{renderIndexMenu(data['display']['device'], item['device'], filteredSearch['device']['relevant'], data.index.device, data)}</div>
          <div onClick={e => handleFocus(e, "phones", "name")}>{renderList(item.device, data['display']['device'], pageNumber, data)}</div>
          <div>{renderIndexMenu(data['display']['user'], item['user'], filteredSearch['user']['relevant'], data.index.user, data)}</div>
          <div onClick={e => handleFocus(e, "users", "userid")}>{renderList(item.user, data['display']['user'], pageNumber, data)}</div>
          <div>{renderIndexMenu(data['display']['did'], item['did'], filteredSearch['did']['relevant'], data.index.did, data)}</div>
          <div>{renderList(item.did, data['display']['did'], pageNumber, data)}</div>
        </div>
        {/* <Pages data={data}/> */}
      </div>
    </div>
  );
};
