export default function getTotalCount(filteredSearch, data, ret=['device', 'user', 'did']) {
  let results = []
  ret.forEach(e => {
      results[e] = Math.ceil(filteredSearch[e]['results'].length/100)
      data['display'][e]['total'] = Math.ceil(filteredSearch[e]['results'].length/100)
  })

  return [data, results]

